<?php # Script 1.0 - checklogon.php	// This page will check the database for the staffer. If they choose to save the information then	// we will create a cookie to save the email address.	//Check the posting of the page to make sure is was not by-passed	if (isset($_POST['Submit'])) {		//Get the information from the logon form		$chrUsername = $_POST['chrUsername'];		$chrPassword = $_POST['chrPassword'];				//check the username and password		if (($chrUsername == 'apple') and ($chrPassword == 'proapps')) {			session_start(); //Start the session.			$_SESSION['idOK'] = 1;			header ("Location: index.php");			exit();		} else {			header ("Location: error.php");			exit();		}	}	header ("Location: error.php");?>