event_result  = mysql_query("SELECT chrEvent FROM corpevents WHERE idEvent = '{$_REQUEST['idAdminEvent']}'");	$event_record = mysql_fetch_array($event_result, MYSQL_ASSOC);//create workbook	$filename = $event_record['chrEvent'].'.xls';	$workbook = new Spreadsheet_Excel_Writer($filename);	//create worksheet	$worksheet =& $workbook->addWorksheet();	$worksheet->setFirstSheet();	$worksheet->setColumn(0,count($wksht_headers),15);	$worksheet->repeatRows(0);	$worksheet->hideGridLines();//create format for fields	$format_header =& $workbook->addFormat();		$format_header->setBold();		$format_header->setSize(12);		$format_header->setAlign('center');			$format_data =& $workbook->addFormat();    	$format_data->setSize(10);		$format_data->setAlign('left');		// writing header cells	$i = 0;	foreach($wksht_headers as $header)	{		//void Worksheet::write (integer $row, integer $col, mixed $token [, mixed $format=0])		$worksheet->write(0, $i, $header, $format_header);		$i++;	}// writing data to cells	$row = 1;	while ($record = mysql_fetch_array($attendees_result, MYSQL_ASSOC)) 	{		$column = 0;		foreach($column_names as $field)		{			$worksheet->write($row, $column, $record[$field], $format_data);			$column++;		}		$row++;	}//close file	$workbook->close();////Finished creating Excel file//===================================================?><!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN" "http://www.w3.org/TR/html4/loose.dtd"><html><head><?php	include('includes/title-meta.htm');?></head><body bgcolor="#8993A3" topmargin="0" leftmargin="0" marginheight="0" marginwidth="0" alink="#0000FF"><?php	include('includes/top.htm');?><table width="698" border="0" align="center" cellpadding="0" cellspacing="0">  <tr>    <td width="10" background="images/fadeleft.gif"><img src="images/fadeleft.gif" width="10" height="1"></td>    <td bgcolor="#ffffff">      <table width="100%"  border="0" cellspacing="0" cellpadding="10